import React from "react";

export default function Form() {
  const { container, inputStyle } = styles;

  return (
    <div className="center" style={container}>
      <div className="input-field">
        <input style={inputStyle}
          autoFocus
          min="1"
          max="100"
          step="1"
          id="inputInit"
          type="number"
        />
        <button className="waves-effect waves-ligth btn">Calcular</button>
        <label htmlFor="inputInit">Quantidade de sorteios:</label>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  },
  inputStyle: {
    width: "300px",
    marginLeft: "30px",
  },
};
