import React from "react";

export default function Form() {
  const { containerStyle} = styles;

  return (
    <div className="center" style={containerStyle}>
      <div className="input-field">
        <input 
        min="1"
        max="100"
        step="1"
        id="inputInit" 
        type="number" 
        className="validate" />

        <label htmlFor="inputInit">Quantidade de sorteios</label>
        <button className="btn btn-primary btn-sm6">Calcular</button>
      </div>
    </div>
  );
}

const styles = {
  containerStyle:{
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  }
}
