import React from 'react'

export default function Number({number}) {
  const{description, value, count} = number;

  return (
    <div>
      <span>{description}</span>
      <span>{count}</span>
    </div>
  )
}

const styles ={
  container: {
    border: "1px solid lightgray",
    borderRadius:"4px",
    padding: "4px",
    margin: "5px",

    display: "flex",
    flexDirection: ""
  }
}
