import React from "react";

export default function Form() {
  const { containerStyle } = styles;

  return (
    <div className="container" style={containerStyle}>
      <div className="input-field">
        <input min="1" max="100" step="1" id="inputInit" type="number" />
        <label htmlFor="inputInit">Quantidade de sorteios</label>
        <button className="waves-effect waves-ligth btn">Calcular</button>
      </div>
    </div>
  );
}

const styles = {
  constainerStyle: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  },
};
