import React from "react";

export default function Form() {
  const {containerStyle, inputStyle, labelStyle} = styles;

  return (
    <div className="container" style={containerStyle}>
      <div className="input-field">
        <input style={inputStyle}
        onfo
        min="1"
        max="100"
        step="1"
        id="inputInit" 
        type="number" 
        className="validate" />

        <label styles={labelStyle} htmlFor="inputInit">Quantidade de sorteios</label>
        <button className="waves-effect waves-light btn">Calcular</button>
      </div>
    </div>
  );
}

const styles = {
  containerStyle:{
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
    marginRight: "10px",
  },
  inputStyle:{
    width:"300px",
    marginLeft: "2px"
  },
  labelStyle:{
    marginRight: "10px"
  }
}
