import React from "react";

export default function Form() {
  const { containerStyle, inputStyle, labelStyle } = styles;

  return (
    <div className="container" style={containerStyle}>
      <div className="input-field">
        <input
          style={inputStyle}
          min="1"
          max="100"
          step="1"
          id="inputInit"
          type="number"
        />

        <label styles={labelStyle} htmlFor="inputInit">
          Quantidade de sorteios
        </label>
      </div>
        <button className="waves-effect waves-light btn">Calcular</button>
    </div>
  );
}

const styles = {
  containerStyle: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  },
  inputStyle: {
    width: "300px",
    marginLeft: "30px",
  },
  labelStyle: {
    marginLeft: "30px",
  },
};
