import React from "react";

export default function Form() {
  const { containerStyle, inputStyle, labelStyle } = styles;

  return (
    <div className="container" style={containerStyle}>
      <div>
        <input style={inputStyle} min="1" max="100" step />
      </div> 
    </div>
  );
}

const styles = {
  containerStyle: {
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  },
  inputStyle: {
    width: "300px",
    marginLeft: "30px",
  },
  labelStyle: {
    marginLeft: "30px",
  },
};
