import React from "react";

export default function Form() {
  const { containerStyle, inputStyle} = styles;

  return (
    <div className="center" style={containerStyle}>
      <div className="input-field">
        <input style={inputStyle}
        min="1"
        max="100"
        step="1"
        id="inputInit" 
        type="number" 
        className="validate" />

        <label htmlFor="inputInit">Quantidade de sorteios</label>
      </div>

        <button className="waves-ef-">Calcular</button>
    </div>
  );
}

const styles = {
  containerStyle:{
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  },
  inputStyle:{
    width:"500px",
    marginLeft: "30px",
  }
}
