import React from "react";

export default function Form() {
  const { container, inputStyle } = styles;

  return (
    <div className="center" style={container}>
      <div className="input-field">
        <input style={inputStyle} min="1" max="100" step="1" id="inputLimit" type="number" />
        <label style={inputStyle} htmlFor="inputLimit">Quantidades de sorteios</label>
      </div>

    <button  className="waves-effect waves-light btn">Calcular</button>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  },
  inputStyle: {
    width: "300px",
    marginLeft: "30px",
  },
  inputStyle: {
    marginLeft: "30px",
  },
};
