import React from "react";

export default function Form() {
  const { container } = styles;

  return (
    <div className="center" style={container} >
      <div className="input-field">
        <input autoFocus min="1" max="100" step="1" id="inputInit" type="number" />
        <label htmlFor="inputInit">Quantidade de sorteios</label>
      </div>
        
        <button className="waves-effect waves-ligth btn">Calcular</button>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  },
  inputStyle: {
    width: "300px",
    marginLeft: "30px"
  }
}
